func:
let battery = msg.payload;
let indicator = battery/100*9.2604027;
let color = "white";
if (battery<30){
    color = "red";
}
else if(battery<50){
    color = "orange";
}


msg = {payload: [
    {"command":"update_text",
    "selector":`#tspan18531-5-9-3, #tspan18531-5-9, #tspan18531-5-9-1`,
    "textContent":`${battery}`},
    {"command":"update_style",
    "selector":`#rect2337-0-8-9, #rect2337-0-8, #rect2337-0-8-7`,
    "style":{"width":`${indicator}`, "fill": `${color}`}}
    ]}

return msg;

let layer1 = "none"
let layer2 = 'none'
let layer3 = "none"
let layer = flow.get("layer");
layer += msg.payload;

if (layer>3){
    layer = 1;
}
else if (layer<1){
    layer = 3
} 
flow.set("layer", layer);


if (layer == 1){
    layer1 = 'inline';
} else if (layer == 2){
    layer2 = 'inline';
} else if (layer == 3){
    layer3 = 'inline';
}

msg = {payload: 
    [{'command': 'set_style',
    'selector': '#layer1',
    'attributeName': 'display',
    'attributeValue': `${layer1}`},
    {'command': 'set_style',
    'selector': '#layer2',
    'attributeName': 'display',
    'attributeValue': `${layer2}`},
    {'command': 'set_style',
    'selector': '#layer3',
    'attributeName': 'display',
    'attributeValue': `${layer3}`}
]}

return msg;
